mapper.select:
select 

from TBL_F_TXN
where SYS_DT = #{sysDt,jdbcType=VARCHAR}
  and SYS_SEQ = #{sysSeq,jdbcType=VARCHAR}

select 

from TBL_F_TXN
where CUP_SSN = #{sysTN,jdbcType=VARCHAR}
  and TXN_DT_TM = #{sysDt,jdbcType=VARCHAR}

mapper.delete:
delete from TBL_F_TXN
where SYS_DT = #{sysDt,jdbcType=VARCHAR}
  and SYS_SEQ = #{sysSeq,jdbcType=VARCHAR}

