mapper.select:
select 

from TBL_AUTH
where SYS_DT = #{sysDt,jdbcType=VARCHAR}
  and SYS_SEQ = #{sysSeq,jdbcType=VARCHAR}

select 

from TBL_AUTH
where CUP_SSN = #{param1,jdbcType=VARCHAR}
  and TXN_DT_TM = #{param2,jdbcType=VARCHAR}

  select 

  from TBL_AUTH
  where AUTH_RSP_CD = #{param1,jdbcType=VARCHAR}
	 	and TXN_TP = #{param2,jdbcType=VARCHAR}

	select AUTH_RSP_CD
	from TBL_AUTH
  where PAN = #{pan,jdbcType=VARCHAR}
  and ACQ_ID = #{acqId,jdbcType=VARCHAR}

  and AUTH_RSP_CD is not null
  and TXN_CD IN ('300002', '400012')

    SELECT 

   	FROM TBL_AUTH
     WHERE


    	TXN_CD IN( '400012' ,'300002') 
  	and 
  		PAN = #{pan,jdbcType=VARCHAR}	
	and
		MCHT_TP = #{mchtTp,jdbcType=VARCHAR}	
	and 
		ACQ_INS_CD = #{acqInsCd,jdbcType=VARCHAR}	
	and 
		FWD_INS_CD = #{fwdInsCd,jdbcType=VARCHAR}	
	and 
		AUTH_RSP_CD = #{authRspCd,jdbcType=VARCHAR}
	and
		ACQ_ID = #{acqId,jdbcType=VARCHAR}	
	and
		CUR_TXN = #{curTxn,jdbcType=VARCHAR}

mapper.delete:
delete from TBL_AUTH
where SYS_DT = #{sysDt,jdbcType=VARCHAR}
  and SYS_SEQ = #{sysSeq,jdbcType=VARCHAR}

mapper.update:
	UPDATE TBL_AUTH

		IC_DATA = #{ICCData,jdbcType=VARCHAR}

	where 
		SYS_DT = #{sysDt,jdbcType=VARCHAR}
  	AND
  	 	SYS_SEQ = #{sysSeq,jdbcType=VARCHAR}

update TBL_AUTH set 
  STEP_INTO_CD = #{txnCd,jdbcType=VARCHAR},
  STEP_INTO_DESC = #{txnDesc,jdbcType=VARCHAR}
where SYS_DT = #{sysDt,jdbcType=VARCHAR}
  and SYS_SEQ = #{sysSeq,jdbcType=VARCHAR}

update TBL_AUTH
set 
  CAN_FLG = #{canFlg,jdbcType=VARCHAR},
  CAN_SEQ = #{processSeq,jdbcType=VARCHAR}
where SYS_DT = #{sysDt,jdbcType=VARCHAR}
  and SYS_SEQ = #{sysSeq,jdbcType=VARCHAR}

update TBL_AUTH
set 
  REV_FLG = #{rwlFlg,jdbcType=VARCHAR},
  REV_SEQ = #{processSeq,jdbcType=VARCHAR}
where SYS_DT = #{sysDt,jdbcType=VARCHAR}
  and SYS_SEQ = #{sysSeq,jdbcType=VARCHAR}

update TBL_AUTH
set 
  FNS_FLG = #{fnsFlg,jdbcType=VARCHAR},
  FNS_SEQ = #{processSeq,jdbcType=VARCHAR}
where SYS_DT = #{sysDt,jdbcType=VARCHAR}
  and SYS_SEQ = #{sysSeq,jdbcType=VARCHAR}

mapper.select:
SELECT ROWNUM, 

FROM
  (SELECT 

 	FROM TBL_AUTH
 	WHERE ORG_SYS_DT = #{orgSysDt,jdbcType=VARCHAR}
    and ORG_SYS_SEQ = #{orgSysSeq,jdbcType=VARCHAR}
    and TXN_CD = #{txnCd,jdbcType=VARCHAR}
  ORDER BY SYS_SEQ DESC)
WHERE ROWNUM = 1
