mapper.select:
select 

from TBL_RFD
where 
	MSG_TP=#{msgTp}
  and CUP_SSN = #{cupSsn,jdbcType=VARCHAR}
  and TXN_DT_TM = #{txnDtTm,jdbcType=VARCHAR}
  and ACQ_INS_CD=#{acqInsCd}
  and FWD_INS_CD=#{fwdInsCd}

select  count(*)    from TBL_RFD 
	where 
		MSG_TP = #{msgTp} 
	and PRC_CD = #{prcCd} 
	and TXN_DT_TM = #{txnDtTm} 
	and CUP_SSN = #{cupSsn} 
	and ACQ_INS_CD = #{acqInsCd} 
	and FWD_INS_CD = #{fwdInsCd}
