mapper.delete:
delete from BTH_DLT_ERR_HIS
where UUID = #{uuid,jdbcType=VARCHAR}
  and DATE_SETTLMT = #{dateSettlmt,jdbcType=VARCHAR}

mapper.select:
	SELECT  rn,  

	FROM	 (SELECT ROWNUM rn,  

			  FROM (
				    SELECT 

				   FROM 
				   		BTH_DLT_ERR_HIS 
				   WHERE 
				   		DATE_SETTLMT = #{dateSettlmt,jdbcType=VARCHAR}
				   		AND
				   			TRANS_DIRECTION !='0'

					       and PAN =#{pan}


					       and MER_ID =#{merId}


					       and TEM_ID =#{temId}


					       and DISPOSE_STATUS =#{disposeStatus}


					       and FUND_FLG =#{fundFlg}


					       and INCOME_STATUS  =#{incomeStatus}


					       and  EXPEND_STATUS=#{expendStatus}


					       and KH_STATUS=#{khStatus}

		      		ORDER BY DATE_SETTLMT,UUID DESC 
	      		)
      		) 
		 WHERE 
		 	  rn   > #{beginNo}    AND  rn   <= #{endNo}


   		  SELECT 
				   		COUNT (1)
				   FROM 
				   		BTH_DLT_ERR_HIS 
				   WHERE 
				   		DATE_SETTLMT = #{dateSettlmt,jdbcType=VARCHAR}
				   		AND
				   			TRANS_DIRECTION !='0'

					       and PAN =#{pan}


					       and MER_ID =#{merId}


					       and TEM_ID =#{temId}


					       and DISPOSE_STATUS =#{disposeStatus}


					       and FUND_FLG =#{fundFlg}


					       and INCOME_STATUS  =#{incomeStatus}


					       and  EXPEND_STATUS=#{expendStatus}


					       and KH_STATUS=#{khStatus}

		      		ORDER BY DATE_SETTLMT,UUID DESC

mapper.update:
update 
	BTH_DLT_ERR_HIS
SET
    DISPOSE_STATUS =#{disposeStatus}
