	UUID = #{uuid,jdbcType=VARCHAR}
and 
	DATE_SETTLMT = #{dateSettlmt,jdbcType=VARCHAR}

update 
	BTH_DLT_ERR_HIS
SET
    INCOME_STATUS  =#{incomeStatus}
where 
	UUID = #{uuid,jdbcType=VARCHAR}
and 
	DATE_SETTLMT = #{dateSettlmt,jdbcType=VARCHAR}

update 
	BTH_DLT_ERR_HIS
SET
    EXPEND_STATUS=#{expendStatus}
where 
	UUID = #{uuid,jdbcType=VARCHAR}
and 
	DATE_SETTLMT = #{dateSettlmt,jdbcType=VARCHAR}

update 
	BTH_DLT_ERR_HIS
SET
    KH_STATUS=#{khStatus}
where 
	UUID = #{uuid,jdbcType=VARCHAR}
and 
	DATE_SETTLMT = #{dateSettlmt,jdbcType=VARCHAR}
